import React, { useState } from 'react';
import Autosuggest from 'react-autosuggest';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';

const FulltextInput = () => {
    const [value, setValue] = useState('');
    const [suggestions, setSuggestions] = useState([]);
    const [suggestionsMap, setSuggestionsMap] = useState({});

    const fetchSuggestions = async (input) => {
        try {
            const response = await fetch(`http://localhost:5000/api/fulltext?input=${input}`);
            const data = await response.json();
            return data;
          } catch (error) {
            console.error('Error fetching data:', error);
            return null; // Return null or some default value in case of error
          }
    }

    const handleSuggestions = async (value) => {

        if ( suggestionsMap[inputValue] ) {
            setSuggestions([]);
            return [];
        } else { // undefined
            const suggestions = await fetchSuggestions(inputValue);
            setSuggestionsMap(
                prevSuggestions => ({
                    ...prevSuggestions,
                    [inputValue]: suggestions
                })
            )
            setSuggestions(suggestions);
            return suggestions;
        }
    }

    const onSuggestionsFetchRequested = async ({ value }) => {
        const inputSuggestions = await handleSuggestions(value);
        setSuggestions(inputSuggestions);
      };

    const fetchData = (event) => {
        event.preventDefault();
        
        const inputValue = event.target.fsInput.value;

        console.log(inputValue);
    }


    return (
        <Autosuggest
        suggestions={suggestions}
        onSuggestionsFetchRequested={onSuggestionsFetchRequested}
        onSuggestionsClearRequested={onSuggestionsClearRequested}
        getSuggestionValue={getSuggestionValue}
        renderSuggestion={renderSuggestion}
        inputProps={inputProps}
        />
    );
