import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSortUp, faSortDown } from '@fortawesome/free-solid-svg-icons';

import './movies-result.css';

const MoviesResult = () => {

    const [rows, setRows] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [sortOrder, setSortOrder] = useState("asc");
    const [sortBy, setSortBy] = useState("rating");
    const perPage = 10;
    const urlParams = new URLSearchParams(useLocation().search);

    useEffect(() => {
        fetchDataManager(urlParams);
        console.log(rows);
    }, [urlParams]);

    useEffect(() => {
        setCurrentPage(1);
    }, [sortOrder, sortBy])

    const fetchByStartChar = async (startCharacter) => {
        try {
            const params = new URLSearchParams({startCharacter, currentPage, sortOrder, sortBy, perPage});
            const response = await fetch(`http://localhost:5000/api/byStartCharacter?${params}`);
            const jsonData = await response.json();
            // console.log(jsonData)
            setRows(jsonData);
          } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    // Decide which endpoint to call
    const fetchDataManager = async (urlParams) => {
        if (urlParams.get('getCharacter')) {
            fetchByStartChar(urlParams.get('getCharacter'))
        }
    }

    const changeSortOrder = () => {
        console.log(sortOrder);
        if (sortOrder === "asc") {
            setSortOrder("desc");
        } else {
            setSortOrder("asc");
        }
    }


    return (
        <div className="page-content">
            <h1>Movies Result</h1>
            <div style={{display:"flex"}}>
                <label defaultValue={sortBy} onChange={ (e) => {setSortBy(e.target.value)} } htmlFor="sortby">Sort By: 
                <select name="sortby" id="sortby">
                    <option value="rating">Rating</option>
                    <option value="title">Title</option>
                    <option value="year">Release</option>
                    <option value="100">Audi</option>
                </select> 
                </label>
                <button className="sortOrderBtn" onClick={changeSortOrder}>
                    {(sortOrder === "asc") 
                    ? <FontAwesomeIcon icon={faSortUp} rotatation={0} style={{color: "#007bff", }} />
                    : <FontAwesomeIcon icon={faSortDown} rotatation={180} style={{color: "#007bff", }} />
                    }
                </button>
            </div>
            <table className="table table-striped">
                <thead className="thead-dark">
                    <tr>
                        <th scope="col" >Title</th>
                        <th scope="col" >Release Year</th>
                        <th scope="col" >Director</th>
                        <th scope="col" >Genres</th>
                        <th scope="col" >Stars</th>
                        <th scope="col" >Rating</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    </tr>
                </tbody>
            </table>
        </div>
    )
}

export default MoviesResult;
