import { useState, useEffect } from 'react';
import './App.css';
import Login from './components/signin/signin';
import TopMovies from './components/top-movies/top-movies';
import Navbar from './components/navbar/navbar';
import SingleMovie from './components/single-movie/single-movie';
import SingleStar from './components/single-star/single-star';
import { Routes, Route, Navigate } from 'react-router-dom';

function App() {
	const [mySession, setSession] = useState({});

	// useEffect(() => {
    //     console.log( "App.js", typeof mySession, mySession);
    // }, [mySession]);


	const ProtectedRoute = ({ element: Component, ...rest }) => {
		if (!mySession.status) {
			return <Navigate to="/login" replace={true} />;
		  }

		  return "<Component />";
	}

	return (
		<div className="App">
			<Navbar/>
			<div className="content">
				<Routes>
					<Route path="/login" element={<Login mySession={mySession} setSession={setSession}/>}/>
					<Route path="/top-movies" element={<ProtectedRoute element={<TopMovies/>}/>}/>
					<Route path="/single-movie" element={<ProtectedRoute element={<SingleMovie/>}/>}/>
					<Route path="/single-start" element={<ProtectedRoute element={<SingleStar/>}/>}/>
				</Routes>

				{/* <Routes>
					<Route 
						exact path="/top-movies" 
						element={mySession.status 
								? <TopMovies/> 
								: <Navigate to="/login" replace={true} />}/>
				</Routes>

				<Routes>
					<Route 
						exact path="/single-movie" 
						element={mySession.status 
							? <SingleMovie/> 
							: <Navigate to="/login" replace={true} />}/>
				</Routes>
				<Routes>
					<Route 
						exact path="/single-star" 
						element={mySession.status 
							? <SingleStar/> 
							: <Navigate to="/login" replace={true} />}/>
				</Routes> */}
			</div>
		</div>
	);
}

export default App;
