import { useContext } from 'react';
import { useAuth0 } from "@auth0/auth0-react";

import './checkout-success.css';

const CheckoutSuccess = () => {

    const { isAuthenticated, loginWithRedirect } = useAuth0();

    const cart = JSON.parse(localStorage.getItem('cart'));

    return (
        <div className='saleInfo'>
            <>Order #: {}</>
            <h3>Thank you for your order</h3>
            <div className='orderDetails'>
                <h4>Order Details</h4>
                <div className='order'>
                    <table className="cart-items table table-striped">
                        <thead className="thead-dark">
                            <tr>
                            <th scope="col" >Title</th>
                            <th scope="col" >Price</th>
                            <th scope="col" >Quantity</th>
                            <th scope="col" >Total</th>
                            </tr>
                        </thead>
                        <tbody>
                        {cart.items.map((item) => (
                            <tr key={item.id}>
                                <td><Link to={`/single-movie?movieId=${item.id}`} className="link">{item.title}</Link></td>
                                <td>${item.price}</td>
                                <td>{item.quantity}</td>
                                <td>${(parseFloat(item.price) * parseInt(item.quantity)).toFixed(2)}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}

export default CheckoutSuccess;
